package com.joycastle.advertise.admob.adapter.vungle;import android.app.Activity;import android.app.Application;import android.content.Intent;import android.os.Bundle;import android.util.Log;import com.google.android.gms.ads.AdRequest;import com.joycastle.advertise.admob.AMAdvertiseHelper;import com.joycastle.gamepluginbase.AdvertiseDelegate;import com.joycastle.gamepluginbase.InvokeJavaMethodDelegate;import com.joycastle.gamepluginbase.SystemUtil;import com.vungle.mediation.VungleAdapter;import com.vungle.mediation.VungleExtrasBuilder;/** * Created by joye on 2018/1/17. */public class VGAdvertiseHelper  {    private static final String TAG = "VGAdvertiseHelper";    private static VGAdvertiseHelper instance = new VGAdvertiseHelper();    public static VGAdvertiseHelper getInstance() {        return instance;    }    private VGAdvertiseHelper() {}    public void init() {        String spotId = SystemUtil.getInstance().getMetaData("vungle_spot_id");        String videoId = SystemUtil.getInstance().getMetaData("vungle_video_id");        String[] placements = new String[]{spotId, videoId};        Bundle extras = new VungleExtrasBuilder(placements).build();        AMAdvertiseHelper.getInstance().setVungle(VungleAdapter.class, extras);        Log.i(TAG, "vungle adapter is init");    }}