package com.joycastle.gamepluginbase;import android.app.Notification;import android.app.NotificationManager;import android.app.PendingIntent;import android.content.BroadcastReceiver;import android.content.ComponentName;import android.content.Context;import android.content.Intent;import android.content.pm.PackageManager;import android.graphics.BitmapFactory;import android.support.v4.app.NotificationCompat;import android.util.Log;public class NotificationPublisher extends BroadcastReceiver {    public static String NOTIFICATION_ID = "notification-id";    public static String NOTIFICATION_CHANNEL_ID = "notification-channel-id";    public static String NOTIFICATION_TITLE = "notification-title";    public static String NOTIFICATION_MSG = "notification-msg";    public static String NOTIFICATION_WHEN = "notification-when";    public static String NOTIFICATION_EXTRA = "notification-extra";    @Override    public void onReceive(Context context, Intent intent) {        int notificationId = intent.getIntExtra(NOTIFICATION_ID, 0);        String notificationChannelId = intent.getStringExtra(NOTIFICATION_CHANNEL_ID);        String notificationTitle = intent.getStringExtra(NOTIFICATION_TITLE);        String notificationMsg = intent.getStringExtra(NOTIFICATION_MSG);        long notificationWhen = intent.getLongExtra(NOTIFICATION_WHEN, 0);        String notificationExtra = intent.getStringExtra(NOTIFICATION_EXTRA);        PackageManager packageManager = context.getPackageManager();        String packageName = context.getPackageName();        ComponentName componentName = packageManager                .getLaunchIntentForPackage(packageName).getComponent();        Intent notifyIntent = new Intent(Intent.ACTION_MAIN);        intent.addCategory(Intent.CATEGORY_LAUNCHER);        intent.setComponent(componentName);        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_RESET_TASK_IF_NEEDED);        intent.putExtra(NOTIFICATION_EXTRA, notificationExtra);        PendingIntent pendingIntent = PendingIntent                .getActivity(context, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT);        Notification notification = new NotificationCompat                .Builder(context, notificationChannelId)                .setSmallIcon(R.mipmap.ic_launcher)                .setLargeIcon(BitmapFactory.decodeResource(context.getResources(), R.mipmap.ic_launcher))                .setContentTitle(notificationTitle)                .setContentText(notificationMsg)                .setStyle(new NotificationCompat.BigTextStyle().bigText(notificationMsg))                .setContentIntent(pendingIntent)                .setPriority(NotificationCompat.PRIORITY_HIGH)                .setWhen(notificationWhen)                .setAutoCancel(true)                .setDefaults(Notification.DEFAULT_ALL)                .setNumber(1)                .build();        NotificationManager notificationManager =                (NotificationManager) context.getSystemService(Context.NOTIFICATION_SERVICE);        notificationManager.notify(notificationId, notification);    }}